const toggleModeButton=document.getElementById("toggleMode"),body=document.body;function toggleDarkMode(){body.classList.toggle("dark-mode"),body.classList.contains("dark-mode")?(localStorage.setItem("mode","dark"),toggleModeButton.innerHTML='<i class="fas fa-sun"></i>'):(localStorage.setItem("mode","light"),toggleModeButton.innerHTML='<i class="fas fa-moon"></i>')}toggleModeButton.addEventListener("click",toggleDarkMode);const savedMode=localStorage.getItem("mode");"dark"===savedMode||!savedMode&&window.matchMedia("(prefers-color-scheme: dark)").matches?toggleDarkMode():toggleModeButton.innerHTML='<i class="fas fa-moon"></i>';